  &lt;/div&gt;
&lt;/template&gt;

&lt;script&gt;
  require(&#39;weex-components&#39;);
  module.exports = {
    data: {
      // ...
    },
    created: function() {
      var vm = this;
      vm.$on(&#39;tabBar.onClick&#39;,function(e){
        var detail= e.detail;
